import React from 'react';
import { StyleSheet, View } from 'react-native';
import { Button, Card, Modal, Text } from '@ui-kitten/components';

const styles = StyleSheet.create({
    container: {
        width: 300
    },
    backdrop: {
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
    },
    button: {
        marginHorizontal: 2,
    },
    buttons: {
        marginTop:0,
        flexDirection: 'row',
        justifyContent: 'flex-end',
    }
});


const DoctorDetails = ({ doctor, isShown, onHide }) => {

    
const Header = (props) => (
    <View {...props}>
        <Text category='h6'>{doctor.name}</Text>
        <Text category='s1'>{doctor.specialization}</Text>
    </View>
)

const Footer = (props)=> (
    <View {...props} style={styles.buttons}>
        <Button style={styles.button} onPress={onHide}>
            Deny
        </Button>
        <Button style={styles.button} onPress={onHide}>
            Verify
        </Button>
    </View>
)

    return (
        <Modal
            style={styles.container}
            visible={isShown}
            backdropStyle={styles.backdrop}
            onBackdropPress={onHide}>
            <Card disabled={true} header={Header(doctor)} footer={Footer(onHide)}>
                <Text>License Number: Insert Image</Text>
                {/* <View style={styles.buttons}>
                    <Button style={styles.button} onPress={onHide}>
                        Deny
                    </Button>
                    <Button style={styles.button} onPress={onHide}>
                        Verify
                    </Button>
                </View> */}
            </Card>
        </Modal>
    );
};

export default DoctorDetails
